import React, { useEffect } from "react";
import styles from "./Contact.module.css";

class FormHandler {
  constructor(formId) {
    this.email = "nayarakarine.araujo@gmail.com"; 
    this.form = document.getElementById(formId);
    this.form.addEventListener("submit", this.handleFormSubmit.bind(this));
  }

  handleFormSubmit(event) {
    event.preventDefault(); // Impede o comportamento padrão de envio do formulário

    const name = document.getElementById("name").value;
    const email = document.getElementById("email").value;
    const message = document.getElementById("message").value;

    const emailMessage = `Nome: ${name}\nE-mail: ${email}\nMensagem: ${message}`;

    this.sendEmail(emailMessage);
    this.clearFormFields();
  }
  sendEmail(message) {
    const form = new FormData();
    form.append("message", message);
  
    fetch("https://formspree.io/f/meqyyezp", {
      method: "POST",
      body: form,
    })
    .then(response => {
      if (!response.ok) {
        // Se a resposta não for 2xx, lança um erro
        return response.json().then(data => {
          throw new Error(data.error || "Erro ao enviar a mensagem.");
        });
      }
      // Se a resposta for bem-sucedida
      alert("Mensagem enviada com sucesso!");
    })
    .catch(error => {
      console.error("Erro:", error);
      alert("Houve um erro ao enviar a mensagem: " + error.message);
    });
  }
  
  

  clearFormFields() {
    document.getElementById("name").value = "";
    document.getElementById("email").value = "";
    document.getElementById("message").value = "";
  }
}

export default function Contact() {
  useEffect(() => {
    const formHandler = new FormHandler("contact-form");
    
    // Cleanup function to remove event listener
    return () => {
      formHandler.form.removeEventListener("submit", formHandler.handleFormSubmit);
    };
  }, []);

  return (
    <div className={styles.contact_container}>
      <h1>Contato</h1>

      <section>
        <h2>Entre em Contato</h2>
        <p>
          Estou disponível para novos projetos e oportunidades. Sinta-se à
          vontade para entrar em contato comigo!
        </p>
        <form id="contact-form">
          <input
            type="text"
            name="name"
            id="name"
            placeholder="Seu Nome"
            required
          />
          <input
            type="email"
            name="email"
            id="email"
            placeholder="Seu Email"
            required
          />
          <textarea
            name="message"
            id="message"
            placeholder="Sua Mensagem"
            required
          ></textarea>
          <button type="submit">
            Enviar Mensagem
          </button>
        </form>
      </section>
    </div>
  );
}
