import React, { useState } from "react"; // Import useState
import projectsData from "../ProjectsData.json";
import styles from "./Projects.module.css";

export default function Projects() {
  const [showBackProjects, setShowBackProjects] = useState(false); // Estado para controlar a visibilidade
  const projectsFront = projectsData.projects.front; 
  const projectsBack = projectsData.projects.back;
  const projectsFullstack = projectsData.projects.fullstack;

  // Função para alternar a visibilidade dos projetos de back-end
  const toggleBackProjects = () => {
    setShowBackProjects(!showBackProjects); // Alterna o estado entre verdadeiro e falso
  };

  return (
    <div className={styles.projects_container}>
      <h1>Projetos</h1>

      {/* Seção de Front-end */}
      <section id="front">
        <h2>Projetos de Front-end</h2>
        <div className={styles.projects_grid}>
          {projectsFront.map((project) => (
            <div key={project.id} className={styles.project_card}>
              <h3>{project.title}</h3>
              <p><strong>Descrição:</strong> {project.description}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Seção de Back-end com controle de visibilidade */}
      <section id="back">
        <h2 onClick={toggleBackProjects} style={{ cursor: 'pointer' }}>
          Projetos de Back-end {showBackProjects ? '⬆️' : '⬇️'}
        </h2>

        {/* Mostrar ou esconder a seção de acordo com o estado */}
        {showBackProjects && (
          <div className={styles.projects_grid}>
            {projectsBack.map((project) => (
              <div key={project.id} className={styles.project_card}>
                <h3>{project.title}</h3>
                <p><strong>Descrição:</strong> {project.description}</p>
                <div className={styles.project_links}>
                  {project.links.project && (
                    <a
                      href={project.links.project}
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      Ver Projeto
                    </a>
                  )}
                  {project.links.project && project.links.repository && (
                    <span> | </span>
                  )}
                  {project.links.repository && (
                    <a
                      href={project.links.repository}
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      Ver Repositório
                    </a>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </section>

      {/* Seção de FullStack */}
      <section id="fullstack">
        <h2>Projetos de FullStack</h2>
        <div className={styles.projects_grid}>
          {projectsFullstack.map((project) => (
            <div key={project.id} className={styles.project_card}>
              <h3>{project.title}</h3>
              <p><strong>Descrição:</strong> {project.description}</p>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}
